import React from 'react';
import { getLatestNotification } from './utils';
import closeIcon from './close-icon.png'; // Make sure the path is correct

function Notifications() {
  const notification = getLatestNotification();

  const handleCloseClick = () => {
    console.log('Close button has been clicked');
  };

  return (
    <div style={{ position: 'relative' }} className="Notifications">
      <p>Here is a list of notifications</p>
      <ul>
        <li data-priority="default">New course available</li>
        <li data-priority="urgent">New resume available</li>
        <li data-priority="urgent" dangerouslySetInnerHTML={{ __html: notification }} />
      </ul>
      <button aria-label='Close' onClick={handleCloseClick}
        style={{
          background: 'none',
          border: 'none',
          padding: '0.3rem',
          cursor: 'pointer',
          position: 'absolute',
          right: '0',
          top: '0'
        }} >
        <img src={closeIcon} alt="Close" style={{ width: '20px', height: '20px' }} />
      </button>
    </div>
  );
}

export default Notifications;
